export const Month = () => {
  let { source, jalali, numberOfMonths, TitleComponent } = React.useContext(
    Context,
  );

  let months = [];
  for (let i = 0; i < numberOfMonths; i++) {
    if (source.get('day') === 0) {
      source = source.add(1, 'day');
    }
    months.push(
      <div className="month date-picker" key={i}>
        {TitleComponent ? (
          <TitleComponent source={source} />
        ) : (
          <TitleOfWeek jalali={jalali} />
        )}
        <Calendar source={source.subtract(-i, 'month')} jalali={jalali}>
          {calendar => (
            <Box column>
              {calendar.map(week => (
                <Box className="week" key={Math.random()}>
                  {week.map(day => {
                    return <Day key={Math.random()} day={day} month={i} />;
                  })}
                </Box>
              ))}
            </Box>
          )}
        </Calendar>
      </div>,
    );
  }

  return (
    <MonthsStyle numberOfMonths={numberOfMonths || 1} jalali={jalali}>
      {months}
    </MonthsStyle>
  );
};
import * as React from 'react';

import { DisplayMonthStyle } from '../index.styles';
import Context from '../context/Context';

const DisplayMonth = () => {
  const { source, setSource, setDisplayMonths, jalali } = React.useContext(
    Context,
  );
  const [months, setMonths] = React.useState([]);
  React.useEffect(() => {
    let months = [];
    for (let i = 0; i < 12; i++) {
      months[i] = source.month(i);
    }
    setMonths(months);
  }, [jalali, source]);

  const updateMonth = source => {
    if (source.get('day') === 0) {
      source = source.add(1, 'day');
    }
    setSource(source.month(+source.subtract(1, 'month').format('M')));
    setDisplayMonths(false);
  };
  return (
    <DisplayMonthStyle jalali={jalali} className="months">
      {months.map(item => {
        return (
          <div
            className="month"
            key={item.format('MMMM')}
            onClick={() => updateMonth(item)}
          >
            <p>{item.format('MMMM')}</p>
          </div>
        );
      })}
    </DisplayMonthStyle>
  );
};

export default DisplayMonth;
import * as React from 'react';
import dayjs from './libs/dayjs';
import { dayjsLocalized } from './libs/dayjs';

import { DatePickerProps } from './typing';

import { Header } from './component/Header';
import { Month } from './date-picker/Month';
import DisplayMonth from './component/DisplayMonth';
import { convertDatesToArray } from './libs';
import Context from './context/Context';

const DatePicker: React.FunctionComponent<DatePickerProps> = ({
  jalali = false,
  numberOfMonths = 1,
  handleChange,
  selectedDays = [],
  numberOfSelectableDays = 0,
  disabledDays = [],
  responsive = undefined,
  disabled = false,
  disabledBeforToday = true,
  dayComponent,
  titleComponent,
}) => {
  const [cloneselectedDays, setSelectedDays] = React.useState(selectedDays);
  const [numberOfMonth, setNumberOfMonth] = React.useState(numberOfMonths);
  const [displayMonths, setDisplayMonths] = React.useState(false);

  const [source, setSource] = React.useState(dayjsLocalized(jalali));

  React.useEffect(() => {
    if (selectedDays.length > 0) {
      let firstDate = selectedDays.sort((prev, next) => {
        return dayjs(prev).isSameOrBefore(next) ? -1 : 1;
      })[0];

      let diffDay = 0;
      diffDay = (dayjs(firstDate) as any).diff(
        (dayjs() as any).format('YYYY-MM-DD'),
        'day',
      );
      setSource(
        (dayjs() as any)
          .add(diffDay, 'day')
          .calendar(jalali ? 'jalali' : 'gregory')
          .locale(jalali ? 'fa' : 'en'),
      );
    } else {
      setSource(dayjsLocalized(jalali));
    }
  }, [jalali]);

  React.useEffect(() => {
    if (selectedDays) {
      if (jalali) {
        setSelectedDays(convertDatesToArray(selectedDays, jalali));
      } else {
        setSelectedDays(selectedDays);
      }
    }
  }, []);

  React.useEffect(() => {
    const handleResize = () => {
      responsive(setNumberOfMonth);
    };
    if (responsive) {
      responsive(setNumberOfMonth);
      if (typeof window !== 'undefined') {
        //handle ssr
        window.addEventListener('resize', handleResize);
      }
    } else {
      setNumberOfMonth(numberOfMonths);
    }
    return () => {
      if (typeof window !== 'undefined') {
        window.removeEventListener('resize', handleResize);
      }
    };
  }, [numberOfMonths]);

  return (
    <div className="tp-calendar">
      <Context.Provider
        value={{
          handleChange,
          selectedDays: cloneselectedDays,
          disabledDays,
          displayMonths,
          setDisplayMonths,
          numberOfSelectableDays,
          disabled,
          disabledBeforToday,
          setSelectedDays,
          numberOfMonths: numberOfMonth,
          jalali,
          source,
          setSource,
          DayComponent: dayComponent,
          TitleComponent: titleComponent,
        }}
      >
        <Header />
        {displayMonths ? <DisplayMonth /> : <Month />}
      </Context.Provider>
    </div>
  );
};

export default DatePicker;
const fontSize = (fs: number): string => fs / 14 + 'rem';

const theme = {
  primary: '#13c8b5',
  primaryHover: '#12baa9',
  secondary: '#757575',
  underlineColor: '#d0f4f0',
  disabledText: '#E0E0E0',
  dir: 'rtl',
  fs16: fontSize(16),
  fs14: fontSize(14),
  fs13: fontSize(13),
  fs12: fontSize(12),
  fs11: fontSize(11),
};

